import React from 'react'

class GoodsInfo extends React.Component{
    render(){
        return(
        <li>{this.props.name} {this.props.thumbnail} {this.props.price}</li>
        )
    }
}

function Shop() {

    let items

    fetch('api/goods/')
        .then(response =>  response.json())
        .then(shop_json => {
            items = shop_json
            console.log(items) 
        })
    return (<div>
        <ul>
            {items.map((item,i) => {
                return (<GoodsInfo name={item.name} price={item.price}/>)
            })}
        </ul>
    </div>
    )
}

export default Shop
